def onStart(chart, step): # WARNING: This resource was generated in a newer version of Ignition. For the best editing experience, recreate it in the current version.
	p1=chart.param1
	p2=chart.param2
	p3=chart.param3
	tagPath=chart.targetPath1

	'''Checks to see if the target ramp value has been achieved'''
	tagVal=system.tag.readBlocking([tagPath])[0].value
	#determine the direction of the ramp up or down
	if p2>=0:
		rampup=True
	else:
		rampup=False

	#if the ramp is upwards, return target if new value is greater than the target, otherwise return the new value

	if rampup:

		if round(tagVal,4)>=p1:
			chart.stepComplete=True

	#if the ramp is downwards, return target if new value is less than the target, otherwise return the new calculated instantantanious ramp value
	else:
		if round(tagVal,4)<=p1:
			chart.stepComplete=True

def onStart(chart, step): # WARNING: This resource was generated in a newer version of Ignition. For the best editing experience, recreate it in the current version. p1=int(chart.param1) p2=int(chart.param2) p3=int(chart.param3)
#check time in step that has been paused and what happens during a pause StepStartTime=chart.stepStartTime pause=chart.stepTotalPause pauseAction=chart.pauseAction
#add all "times" together" StepStartTime=system.date.addSeconds(StepStartTime,pause) timePlusHours=system.date.addHours(StepStartTime,p1) timePlusMinutes=system.date.addMinutes(timePlusHours,p2) endTime=system.date.addSeconds(timePlusMinutes,p3) nowTime=system.date.now() #check if now is after added time if system.date.isAfter(nowTime, endTime): chart.stepComplete=True chart.estEndTime=endTime

def onStart(chart, step): # WARNING: This resource was generated in a newer version of Ignition. For the best editing experience, recreate it in the current version.
	#write a string to a tag
	if chart.writeString is not None:
		tagPath=chart.targetPath1
		system.tag.writeBlocking([tagPath], [chart.writeString])
		val=system.tag.readBlocking([tagPath])[0].value
		if val==chart.writeString:
			chart.stepComplete=True
	if chart.f7Message==False:
		chart.f7Message=True
